import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Admin() {
  const navigate = useNavigate();

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('authToken');
    if (!isAuthenticated) {
      navigate('/login');
    }
  }, [navigate]);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    location: '',
  });

  const [message, setMessage] = useState({ text: '', type: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/opportunities', formData);
      setMessage({ text: 'Opportunity created successfully!', type: 'success' });
      setFormData({ title: '', description: '', duration: '', location: '' });
    } catch (error) {
      setMessage({ text: 'Error creating opportunity. Please try again.', type: 'error' });
    }
  };

  React.useEffect(() => {
    if (message.text) {
      const timer = setTimeout(() => {
        setMessage({ text: '', type: '' });
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  return (
    <div className="min-h-screen bg-gray-600 flex items-center justify-center p-6">
      <div className="w-full max-w-xl">
        <button
          onClick={() => navigate('/')}
          className="mb-6 inline-block bg-blue-500 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          ðŸ”™ Back to Opportunities
        </button>
        <h1 className="text-2xl font-semibold text-green-100 mb-6">Admin Panel - Create Opportunity</h1>
        {message.text && (
          <div
            className={`mb-4 text-center py-2 px-4 rounded transition-all duration-300 ease-in-out ${
              message.type === 'success' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
            }`}
          >
            {message.text}
          </div>
        )}
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Opportunity Title"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              required
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <div className="mb-4">
            <textarea
              placeholder="Description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              required
              className="w-full px-4 py-2 border rounded h-32 resize-none focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Duration (e.g., 3 months)"
              value={formData.duration}
              onChange={(e) => setFormData({ ...formData, duration: e.target.value })}
              required
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Place"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              required
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <button
            type="submit"
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
          >
            Create Opportunity
          </button>
        </form>
      </div>
    </div>
  );
}

export default Admin;
